pages/brand:
---
brand:
    products:
        limit: {{theme_settings.brandpage_products_per_page}}

        category:
            shop_by_price: true
            products:
                limit: {{theme_settings.categorypage_products_per_page}}

blog:
    recent_posts:
        limit: {{theme_settings.footer_blog_posts_count}}
---
{{inject "brandProductsPerPage" theme_settings.brandpage_products_per_page}}
{{#partial "head"}}
    {{#if pagination.brand.previous}}
        <link rel="prev" href="{{pagination.brand.previous}}">
    {{/if}}
    {{#if pagination.brand.next}}
        <link rel="next" href="{{pagination.brand.next}}">
    {{/if}}
{{/partial}}

{{#partial "page"}}
{{{region name="brand_below_menu"}}}
{{> components/custom/brand-sidenav}}

<div class="brand main {{#if theme_settings.top_pagination_visibility '===' false}}top-pagination-hidden {{/if}} {{#if theme_settings.bottom_pagination_visibility '===' false}}bottom-pagination-hidden {{/if}} {{#if brand.faceted_search_enabled}} product-filtering-enabled {{else}} product-filtering-disabled{{/if}}">
{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
<h1 class="container-header">{{brand.name}}</h1>
  {{{region name="brand_below_header"}}}
  {{#if banners.top}}
      <div class="banners top" data-banner-location="top">
        {{#each (limit banners.top_metadata 1)}}
          <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-banner-id="{{this.banner-name}}">
            <div data-event-type="promotion-click">
              {{{this.content}}}
            </div>
          </div>
        {{/each}}
      </div>
  {{/if}}
{{#if brand.image}}
<!-- <div class="brand-main-image">
  <img src="{{getImage brand.image 'thumb'}}">
</div> -->
{{/if}}
{{{snippet 'brand'}}}

<!-- GRID/LIST TOGGLE BUTTONS - GRID BY DEFAULT -->
{{#if theme_settings.product_layout_view '===' 'both_grid_default'}}
  <div class="grid-list-view toggle">
    <a class="toggle-link default grid active" href="#" title="Toggle Grid View"><svg class="toggle grid"><use xlink:href="#icon-grid"></use></svg></a>
    <a class="toggle-link list" href="#" title="Toggle List View"><svg class="toggle list"><use xlink:href="#icon-list"></use></svg></a>
    <div class="clear"></div>
  </div>
{{/if}}

<!-- GRID/LIST TOGGLE BUTTONS - LIST BY DEFAULT -->
{{#if theme_settings.product_layout_view '===' 'both_list_default'}}
  <div class="grid-list-view toggle">
    <a class="toggle-link default list active" href="#" title="Toggle List View"><svg class="toggle list"><use xlink:href="#icon-list"></use></svg></a>
    <a class="toggle-link grid" href="#" title="Toggle Grid View"><svg class="toggle grid"><use xlink:href="#icon-grid"></use></svg></a>
    <div class="clear"></div>
  </div>
{{/if}}

<div class="page">
    <main class="page-content {{#if theme_settings.product_layout_view '===' 'both_grid_default'}} both-grid-list-views {{/if}} {{#if theme_settings.product_layout_view '===' 'both_list_default'}} both-grid-list-views{{/if}}" id="product-listing-container">
        {{#if brand.products}}
            {{> components/brand/product-listing columns=theme_settings.category_brand_search_column_count settings=../settings}}
        {{else}}
            <p>{{lang 'brands.no_products'}}</p>
        {{/if}}
        {{{region name="brand_below_content"}}}
    </main>

    <div class="compare">
            <a class="navUser-action navUser-item--compare" href="{{urls.compare}}" data-compare-nav>{{lang 'Compare Selected'}}</a>
    </div>

    <div class="clear"></div>
    {{#if banners.bottom}}
      {{#if settings.data_tag_enabled}}
        <div class="banners bottom" data-banner-location="bottom">
          {{#each (limit banners.bottom_metadata 1)}}
            <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
              <div data-event-type="promotion-click">
                {{{this.content}}}
               </div>
             </div>
           {{/each}}
         </div>
         {{else}}
           <div class="banners bottom" data-banner-location="bottom">
             <div class="banner">
               {{{limit banners.bottom 1}}}
             </div>
           </div>
         {{/if}}
     {{/if}}
     {{{region name="brand_below_bottom_banner"}}}
</div>
</div>

{{/partial}}
{{> layout/base}}

components/brand/product-listing:
{{> components/products/filter sort=pagination.brand.sort}}

{{#if theme_settings.product_layout_view '===' 'grid'}}
  <form class="grid-only" action="{{urls.compare}}" method='POST' {{#if settings.data_tag_enabled}} data-list-name="Brand: {{brand.name}}" {{/if}} data-product-compare>
    {{> components/products/grid products=brand.products show_compare=brand.show_compare theme_settings=theme_settings event="list"}}
  </form>
{{/if}}

{{#if theme_settings.product_layout_view '===' 'list'}}
  <form class="list-only" action="{{urls.compare}}" method='POST' {{#if settings.data_tag_enabled}} data-list-name="Brand: {{brand.name}}" {{/if}} data-product-compare>
    {{> components/products/list products=brand.products show_compare=brand.show_compare theme_settings=theme_settings event="list"}}
  </form>
{{/if}}

{{#if theme_settings.product_layout_view '===' 'both_grid_default'}}
  <form class="both-grid-default" action="{{urls.compare}}" method='POST' {{#if settings.data_tag_enabled}} data-list-name="Brand: {{brand.name}}" {{/if}} data-product-compare>
    {{> components/products/grid-default products=brand.products show_compare=brand.show_compare theme_settings=theme_settings event="list"}}
    {{> components/products/list products=brand.products show_compare=brand.show_compare theme_settings=theme_settings event="list"}}
  </form>
{{/if}}

{{#if theme_settings.product_layout_view '===' 'both_list_default'}}
  <form class="both-list-default" action="{{urls.compare}}" method='POST' {{#if settings.data_tag_enabled}} data-list-name="Brand: {{brand.name}}" {{/if}} data-product-compare>
    {{> components/products/list-default products=brand.products show_compare=brand.show_compare theme_settings=theme_settings event="list"}}
    {{> components/products/grid products=brand.products show_compare=brand.show_compare theme_settings=theme_settings event="list"}}
  </form>
{{/if}}

{{> components/common/paginator pagination.brand}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}" href="{{url}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}">
    {{name}} <i class="fa fa-chevron-down main-nav" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}">
                        {{name}} <i class="fa fa-chevron-down" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/common/footer:
{{{region name="footer_above_full_width--global"}}}
<footer class="footer" role="contentinfo">
	{{#if settings.show_newsletter_box}}
    <div class="footer-top newsletter">
        <div class="content newsletter">
            <div class="" data-section-type="newsletterSubscription">
                    {{> components/common/subscription-form}}
										<div class="clear"></div>
            </div>
      			<div class="clear"></div>
        </div>
    </div>

		<div class="global-region screen-width below-footer-newsletter">
			<div class="global-region body-width">
				{{{region name="footer_below_newsletter--global"}}}
			</div>
		</div>

   {{/if}}
    <div class="container">
        <section class="footer-info">
         <article class="footer-info-col footer-info-col--small" data-section-type="storeInfo">
                <h5 class="footer-info-heading">Contact Us</h5>
                <div class="regular">
                    <address>{{nl2br settings.address}}</address>
                </div>

								<div class="global-region container-width"> <!-- can be used for SSL Certificate Seal -->
									{{{region name="footer_below_contact--global"}}}
								</div>
        </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-webPages">
                <h5 class="footer-info-heading">Accounts & Orders</h5>
                <div class="regular">
                <ul class="footer-info-list">
                	{{#if settings.gift_certificates_enabled}}
                        <li>
                            <a href="/giftcertificates.php">Gift Certificates</a>
                        </li>
                    {{/if}}
										{{#if settings.show_wishlist}}
											<li>
												<a href="/wishlist.php">Wishlist</a>
											</li>
										{{/if}}
                    {{#if settings.account_creation_enabled}}
                        {{#if customer}}
                            <li><a class="navUser-action" href="{{urls.account.index}}">{{lang 'common.account'}}</a></li>
                            <li><a class="navUser-action" href="{{urls.auth.logout}}">{{lang 'common.logout'}}</a></li>
														<li>
				                        <a href="/account.php?action=order_status">Order Status</a>
				                    </li>
                        {{else}}
                            <li><a class="navUser-action" href="{{urls.auth.login}}">{{lang 'common.login'}}</a>
                            {{#if settings.account_creation_enabled}}
                                <span class="navUser-or">{{lang 'common.or'}}</span> <a class="navUser-action" href="{{urls.auth.create_account}}">{{lang 'common.sign_up'}}</a></li>
                            {{/if}}
                        {{/if}}
                    {{/if}}

										{{#if theme_settings.show_shipping_returns_link}}
                    	<li>
												<a href="{{theme_settings.shipping_returns_url}}">{{theme_settings.shipping_returns_link_name}}</a>
                    	</li>
										{{/if}}
                </ul>
                </div>

								<div class="global-region container-width"> <!-- can be used for SSL Certificate Seal -->
									{{{region name="footer_below_account--global"}}}
								</div>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-categories">
                <h5 class="footer-info-heading">Quick Links</h5>
                <div class="regular">
                <ul class="footer-info-list">
                    {{#each pages}}
                    <li>
                        <a href="{{url}}">{{name}}</a>
                    </li>
                    {{/each}}
                </ul>
                </div>

								<div class="global-region container-width">
									{{{region name="footer_below_quick_links--global"}}} <!-- can be used for SSL Certificate Seal -->
								</div>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-categories">

							{{#if settings.blog_enabled}}
                <h5 class="footer-info-heading recent-posts-heading">Recent Blog Posts</h5>
                <div class="blog regular">
                    <ul class="footer-info-list">
                        {{#each (limit blog.recent_posts theme_settings.footer_blog_posts_count)}}
                            <li><a href="{{url}}">{{title}}</a></li>
                        {{/each}}
                    </ul>
                </div>
							{{/if}}

                <div class="social-media">
                    {{> components/common/social-links}}
                </div>

							 <div class="global-region container-width"> 	<!-- can be used for SSL Certificate Seal -->
									{{{region name="footer_below_social_links--global"}}}
							 </div>
            </article>
            <div class="clear"></div>

            {{> components/common/payment-icons}}

						<div class="global-region container-width below-payment-icons"> <!-- can be used for SSL Certificate Seal -->
						  {{{region name="footer_below_payment_icons--global"}}}
						</div>

        </section>

        <div class="clear"></div>
    </div>

		<div class="global-region screen-width above-footer-creditsBar">
			<div class="global-region body-width">
				{{{region name="footer_above_credits_bar--global"}}} <!-- can be used for SSL Certificate Seal -->
			</div>
	</div>

    <div class="footer-bottom">
        <div class="content copyright">
					<ul class="site-info">
							{{#if theme_settings.show_copyright_footer}}
											<li class="copyright"><span class="current-year">&copy; <span id="copyright_year"></span> {{settings.store_name}}</span></li>
							{{/if}}

							{{#if theme_settings.show_copyright_footer '===' true}}
								{{#if theme_settings.show_sitemap_link '===' true}}
									{{#if theme_settings.show_powered_by '===' true}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}
							{{/if}}

							{{#if theme_settings.show_copyright_footer '===' true}}
								{{#if theme_settings.show_sitemap_link '===' true}}
									{{#if theme_settings.show_powered_by '===' false}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}
							{{/if}}

							{{#if theme_settings.show_copyright_footer '===' true}}
								{{#if theme_settings.show_sitemap_link '===' false}}
									{{#if theme_settings.show_powered_by '===' true}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}
							{{/if}}

							{{#if theme_settings.show_sitemap_link}}
								<li class="sitemap"><a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a></li>
							{{/if}}

								{{#if theme_settings.show_sitemap_link '===' true}}
									{{#if theme_settings.show_powered_by '===' true}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}

							{{#if theme_settings.show_powered_by}}
								<li class="built-by">Premium <a target="_blank" href="https://www.bigcommerce.com/">BigCommerce</a> Theme by <a class="lone-star-templates" target="_blank" href="http://lonestartemplates.com/">Lone Star Templates</a></li>
							{{/if}}
						<div class="clear"></div>
						</ul>
        </div>
    </div>

		<div class="global-region screen-width below-footer-creditsBar">
			<div class="global-region body-width">
				{{{region name="footer_below_credits_bar--global"}}} <!-- can be used for SSL Certificate Seal -->
			</div>
		</div>

    <div id="topcontrol" class="backtoTop" title="Scroll Back to Top">
          <svg class="icon back-to-top-icon" title="submit" alt="submit"><use xlink:href="#icon-caret-square-up"></use></svg>
      </div>
    </footer>

components/common/social-links:
{{#if social_media}}
    <h5 class="footer-info-heading">Connect with Us:</h5>
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<div class="currency-converter">
<li class="navUser-item currency-menu">
    <a class="navUser-action has-dropdown" title="Currency Selector" alt="Currency Selector" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{currency_selector.active_currency_code}} <i class="icon currency" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
    <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
        {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a class="currency-name-switcher" href="{{{switch_url}}}">
                {{#if is_active}}
                <strong>{{{flag}}} {{name}}</strong>
                {{else}}
                {{{flag}}} {{name}}
                {{/if}}
            </a>
        </li>
        {{/each}}
    </ul>
</li>
</div>
{{/if}}

components/products/list-item:
{{#if settings.data_tag_enabled}}
    <article class="listItem {{#if show_compare}} compare-enabled {{else}} compare-disabled{{/if}} {{#if alternate}}card--alternate{{/if}} price-visibility{{#or customer (if theme_settings.restrict_to_login '===' true)}} restricted{{/or}} {{#if theme_settings.show_sale_price_label '===' true}}sale-label-active{{/if}} {{#if theme_settings.card-brand-name '===' true}}brand-sku-active brand-active{{/if}} {{#if theme_settings.card-brand-name '===' false}}brand-hidden{{/if}} {{#if theme_settings.card-sku '===' true}}brand-sku-active sku-active{{/if}} {{#if theme_settings.card-sku '===' false}}sku-hidden{{/if}} {{#if theme_settings.card-brand-sku-separate-lines '===' true}}brand-sku-separate-lines{{/if}} {{#if theme_settings.card-brand-2-lines '===' true}}brand-2-lines{{/if}} {{#if theme_settings.card-sku-2-lines '===' true}}sku-2-lines{{/if}} {{#if theme_settings.msrp-price '===' true}}msrp-visible{{/if}} {{#if theme_settings.msrp-before-sale-pricing-separate-lines '===' true}}msrp-and-regular-separate-lines{{/if}} {{#if theme_settings.before-sale-price '===' true}} before-sale-price-visible {{/if}} {{#if theme_settings.before-sale-price '===' false}}before-sale-price-hidden{{/if}} {{#if theme_settings.card-title-lines '===' '1-line'}} card-title-one-line{{/if}} {{#if theme_settings.card-title-lines '===' '2-lines'}}card-title-two-lines{{/if}} {{#if theme_settings.card-title-lines '===' '3-lines'}}card-title-three-lines{{/if}} {{#if theme_settings.card-title-lines '===' '4-lines'}}card-title-four-lines{{/if}} {{#if theme_settings.current-price-on-two-lines '===' true}}current-price-on-two-lines {{/if}} {{#if theme_settings.price_ranges '===' true}}price-ranges-active{{/if}} {{#if theme_settings.card-text-align '===' 'center'}} center-align{{/if}} {{#if price.with_tax}} withTax{{/if}}{{#if price.without_tax}} withoutTax{{/if}}" data-event-type="{{event}}" data-entity-id="{{id}}" data-position="{{position}}" data-name="{{name}}" data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{brand.name}}" data-product-price="{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}">
{{else}}
    <article class="listItem {{#if show_compare}} compare-enabled {{else}} compare-disabled{{/if}} {{#if alternate}}card--alternate{{/if}} price-visibility{{#or customer (if theme_settings.restrict_to_login '===' true)}} restricted{{/or}} {{#if theme_settings.show_sale_price_label '===' true}}sale-label-active{{/if}} {{#if theme_settings.card-brand-name '===' true}}brand-sku-active brand-active{{/if}} {{#if theme_settings.card-brand-name '===' false}}brand-hidden{{/if}} {{#if theme_settings.card-sku '===' true}}brand-sku-active sku-active{{/if}} {{#if theme_settings.card-sku '===' false}}sku-hidden{{/if}} {{#if theme_settings.card-brand-sku-separate-lines '===' true}}brand-sku-separate-lines{{/if}} {{#if theme_settings.card-brand-2-lines '===' true}}brand-2-lines{{/if}} {{#if theme_settings.card-sku-2-lines '===' true}}sku-2-lines{{/if}} {{#if theme_settings.msrp-price '===' true}}msrp-visible{{/if}} {{#if theme_settings.msrp-before-sale-pricing-separate-lines '===' true}}msrp-and-regular-separate-lines{{/if}} {{#if theme_settings.before-sale-price '===' true}} before-sale-price-visible {{/if}} {{#if theme_settings.before-sale-price '===' false}}before-sale-price-hidden{{/if}} {{#if theme_settings.card-title-lines '===' '1-line'}} card-title-one-line{{/if}} {{#if theme_settings.card-title-lines '===' '2-lines'}}card-title-two-lines{{/if}} {{#if theme_settings.card-title-lines '===' '3-lines'}}card-title-three-lines{{/if}} {{#if theme_settings.card-title-lines '===' '4-lines'}}card-title-four-lines{{/if}} {{#if theme_settings.current-price-on-two-lines '===' true}}current-price-on-two-lines {{/if}} {{#if theme_settings.price_ranges '===' true}}price-ranges-active{{/if}} {{#if theme_settings.card-text-align '===' 'center'}} center-align{{/if}} {{#if price.with_tax}} withTax{{/if}}{{#if price.without_tax}} withoutTax{{/if}}">
{{/if}}
    <figure class="listItem-figure">
      {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
              {{#if theme_settings.product_sale_badges '===' 'sash'}}
                  <div class="sale-flag-sash">
                      <span class="sale-text">SALE</span>
                  </div>
              {{else if theme_settings.product_sale_badges '===' 'topleft'}}
                  <div class="sale-flag-side">
                      <span class="sale-text">SALE</span>
                  </div>
              {{else if theme_settings.product_sale_badges '===' 'burst'}}
                  <div class="starwrap">
                      <div class="sale-text-burst">SALE</div>
                      <div class="sale-flag-star"></div>
                  </div>
              {{/if}}
          {{/or}}
        <a class="image-link" href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>
          {{#if theme_settings.product_hover_images '===' 'active'}}
             <img class="listItem-image lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}">
            {{#each (limit images 2)}}
              {{#if @index '>' 0}}
                <img class='listItem-image alt' src="{{getImage this 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}">
              {{/if}}
            {{/each}}
              {{else if theme_settings.product_hover_images '===' 'inactive'}}
                <img class="card-image lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}">
            {{/if}}
            </a>
        {{#unless hide_product_quick_view}}
            {{#if theme_settings.show_product_quick_view}}
                <div class="listItem-figureBody">
                    <a href="#" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small listItem-button quickview" data-product-id="{{id}}">{{lang 'products.quick_view'}}</a>
                </div>
            {{/if}}
        {{/unless}}

    </figure>
    <div class="listItem-body">
        <div class="listItem-content">
            <div class="listItem-details">
                {{#if show_rating}}
