pages/contact-us:
---
blog:
    recent_posts:
        limit: {{theme_settings.footer_blog_posts_count}}
---
{{#partial "page"}}

<main class="page content contact{{#if theme_settings.contact_page_layout '!==' 'no-sidenav'}} contact-page-with-sidenav{{/if}}">

  <div class="contact-page-content">
    {{#if ../theme_settings.contact_page_layout '===' 'no-sidenav'}}
      {{> components/page/contact-page-no-sidenav}}
    {{/if}}
    {{#if ../theme_settings.contact_page_layout '===' 'with-sidenav'}}
      {{> components/page/contact-page-with-sidenav}}
    {{/if}}
    <div class="clear"></div>
  </div>

</main>

{{/partial}}

{{> layout/base}}

components/common/footer:
{{{region name="footer_above_full_width--global"}}}
<footer class="footer" role="contentinfo">
	{{#if settings.show_newsletter_box}}
    <div class="footer-top newsletter">
        <div class="content newsletter">
            <div class="" data-section-type="newsletterSubscription">
                    {{> components/common/subscription-form}}
										<div class="clear"></div>
            </div>
      			<div class="clear"></div>
        </div>
    </div>

		<div class="global-region screen-width below-footer-newsletter">
			<div class="global-region body-width">
				{{{region name="footer_below_newsletter--global"}}}
			</div>
		</div>

   {{/if}}
    <div class="container">
        <section class="footer-info">
         <article class="footer-info-col footer-info-col--small" data-section-type="storeInfo">
                <h5 class="footer-info-heading">Contact Us</h5>
                <div class="regular">
                    <address>{{nl2br settings.address}}</address>
                </div>

								<div class="global-region container-width"> <!-- can be used for SSL Certificate Seal -->
									{{{region name="footer_below_contact--global"}}}
								</div>
        </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-webPages">
                <h5 class="footer-info-heading">Accounts & Orders</h5>
                <div class="regular">
                <ul class="footer-info-list">
                	{{#if settings.gift_certificates_enabled}}
                        <li>
                            <a href="/giftcertificates.php">Gift Certificates</a>
                        </li>
                    {{/if}}
										{{#if settings.show_wishlist}}
											<li>
												<a href="/wishlist.php">Wishlist</a>
											</li>
										{{/if}}
                    {{#if settings.account_creation_enabled}}
                        {{#if customer}}
                            <li><a class="navUser-action" href="{{urls.account.index}}">{{lang 'common.account'}}</a></li>
                            <li><a class="navUser-action" href="{{urls.auth.logout}}">{{lang 'common.logout'}}</a></li>
														<li>
				                        <a href="/account.php?action=order_status">Order Status</a>
				                    </li>
                        {{else}}
                            <li><a class="navUser-action" href="{{urls.auth.login}}">{{lang 'common.login'}}</a>
                            {{#if settings.account_creation_enabled}}
                                <span class="navUser-or">{{lang 'common.or'}}</span> <a class="navUser-action" href="{{urls.auth.create_account}}">{{lang 'common.sign_up'}}</a></li>
                            {{/if}}
                        {{/if}}
                    {{/if}}

										{{#if theme_settings.show_shipping_returns_link}}
                    	<li>
												<a href="{{theme_settings.shipping_returns_url}}">{{theme_settings.shipping_returns_link_name}}</a>
                    	</li>
										{{/if}}
                </ul>
                </div>

								<div class="global-region container-width"> <!-- can be used for SSL Certificate Seal -->
									{{{region name="footer_below_account--global"}}}
								</div>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-categories">
                <h5 class="footer-info-heading">Quick Links</h5>
                <div class="regular">
                <ul class="footer-info-list">
                    {{#each pages}}
                    <li>
                        <a href="{{url}}">{{name}}</a>
                    </li>
                    {{/each}}
                </ul>
                </div>

								<div class="global-region container-width">
									{{{region name="footer_below_quick_links--global"}}} <!-- can be used for SSL Certificate Seal -->
								</div>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-categories">

							{{#if settings.blog_enabled}}
                <h5 class="footer-info-heading recent-posts-heading">Recent Blog Posts</h5>
                <div class="blog regular">
                    <ul class="footer-info-list">
                        {{#each (limit blog.recent_posts theme_settings.footer_blog_posts_count)}}
                            <li><a href="{{url}}">{{title}}</a></li>
                        {{/each}}
                    </ul>
                </div>
							{{/if}}

                <div class="social-media">
                    {{> components/common/social-links}}
                </div>

							 <div class="global-region container-width"> 	<!-- can be used for SSL Certificate Seal -->
									{{{region name="footer_below_social_links--global"}}}
							 </div>
            </article>
            <div class="clear"></div>

            {{> components/common/payment-icons}}

						<div class="global-region container-width below-payment-icons"> <!-- can be used for SSL Certificate Seal -->
						  {{{region name="footer_below_payment_icons--global"}}}
						</div>

        </section>

        <div class="clear"></div>
    </div>

		<div class="global-region screen-width above-footer-creditsBar">
			<div class="global-region body-width">
				{{{region name="footer_above_credits_bar--global"}}} <!-- can be used for SSL Certificate Seal -->
			</div>
	</div>

    <div class="footer-bottom">
        <div class="content copyright">
					<ul class="site-info">
							{{#if theme_settings.show_copyright_footer}}
											<li class="copyright"><span class="current-year">&copy; <span id="copyright_year"></span> {{settings.store_name}}</span></li>
							{{/if}}

							{{#if theme_settings.show_copyright_footer '===' true}}
								{{#if theme_settings.show_sitemap_link '===' true}}
									{{#if theme_settings.show_powered_by '===' true}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}
							{{/if}}

							{{#if theme_settings.show_copyright_footer '===' true}}
								{{#if theme_settings.show_sitemap_link '===' true}}
									{{#if theme_settings.show_powered_by '===' false}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}
							{{/if}}

							{{#if theme_settings.show_copyright_footer '===' true}}
								{{#if theme_settings.show_sitemap_link '===' false}}
									{{#if theme_settings.show_powered_by '===' true}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}
							{{/if}}

							{{#if theme_settings.show_sitemap_link}}
								<li class="sitemap"><a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a></li>
							{{/if}}

								{{#if theme_settings.show_sitemap_link '===' true}}
									{{#if theme_settings.show_powered_by '===' true}}
										<span class="separator"> | </span>
									{{/if}}
								{{/if}}

							{{#if theme_settings.show_powered_by}}
								<li class="built-by">Premium <a target="_blank" href="https://www.bigcommerce.com/">BigCommerce</a> Theme by <a class="lone-star-templates" target="_blank" href="http://lonestartemplates.com/">Lone Star Templates</a></li>
							{{/if}}
						<div class="clear"></div>
						</ul>
        </div>
    </div>

		<div class="global-region screen-width below-footer-creditsBar">
			<div class="global-region body-width">
				{{{region name="footer_below_credits_bar--global"}}} <!-- can be used for SSL Certificate Seal -->
			</div>
		</div>

    <div id="topcontrol" class="backtoTop" title="Scroll Back to Top">
          <svg class="icon back-to-top-icon" title="submit" alt="submit"><use xlink:href="#icon-caret-square-up"></use></svg>
      </div>
    </footer>

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<div class="currency-converter">
<li class="navUser-item currency-menu">
    <a class="navUser-action has-dropdown" title="Currency Selector" alt="Currency Selector" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{currency_selector.active_currency_code}} <i class="icon currency" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
    <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
        {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a class="currency-name-switcher" href="{{{switch_url}}}">
                {{#if is_active}}
                <strong>{{{flag}}} {{name}}</strong>
                {{else}}
                {{{flag}}} {{name}}
                {{/if}}
            </a>
        </li>
        {{/each}}
    </ul>
</li>
</div>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <h5 class="footer-info-heading">Connect with Us:</h5>
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/category/shop-by-price:
{{#if theme_settings.shop_by_price_visibility}}
    <div class="sidebarBlock">
        <h5 class="sidebarBlock-heading">{{lang 'category.shop_by_price'}}</h5>
        <ul class="navList">
            {{#each shop_by_price}}
            <li class="navList-item">
                <a class="navList-action {{#if selected }} is-active {{/if}}" href="{{url}}" alt="{{low.formatted}} - {{high.formatted}}" title="{{low.formatted}} - {{high.formatted}}">{{low.formatted}} - {{high.formatted}}</a>
            </li>
            {{/each}}

            {{#any shop_by_price selected=true}}
                <li class="navList-item">
                    <a href="{{category_url}}" class="navList-action">
                        {{lang 'category.reset'}}
                    </a>
                </li>
            {{/any}}
        </ul>
    </div>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}" href="{{url}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}">
    {{name}} <i class="fa fa-chevron-down main-nav" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}">
                        {{name}} <i class="fa fa-chevron-down" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/faceted-search/facets/multi:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content{{#unless start_collapsed}} is-open{{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" data-facet="{{facet}}" class="navList" data-has-more-results="{{has_more_results}}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected}} is-selected{{/if}}"
                        rel="nofollow"
                        data-faceted-search-facet>
                        {{{sanitize title}}}
                        {{#if ../show_product_counts}}
                            <span>({{count}})</span>
                        {{/if}}

                        <span class="navList-action-close" aria-hidden="true">
                            <svg class="icon">
                                <use xlink:href="#icon-close"/>
                            </svg>
                        </span>
                    </a>
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{dashcase facet}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content{{#unless start_collapsed}} is-open{{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" class="navList" data-facet="{{facet}}" data-has-more-results="{{has_more_results}}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected }} is-selected{{/if}}{{#if has_sub_categories}} has-children{{/if}}"
                        rel="nofollow"
                        data-id="{{id}}"
                        data-faceted-search-facet>
                        {{{sanitize title}}}
                        {{#if ../show_product_counts}}
                            <span>({{count}})</span>
                        {{/if}}
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{dashcase facet}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

components/faceted-search/facets/rating:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content facetedSearch-content--{{dashcase facet}}{{#unless start_collapsed}} is-open{{/unless}}">
        <ul class="navList">
            {{#each items}}
                <li class="navList-item">
                    <a href="{{url}}"
                       class="navList-action{{#if selected}} is-selected{{/if}}"
                       rel="nofollow"
                       data-faceted-search-facet>
                        <span class="rating--small">
                            {{> components/products/ratings rating=title}}
                        </span>
                        {{lang 'search.faceted.rating.and-up'}}
                        {{#if ../show_product_counts}}
                            <span>({{count}})</span>
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
    </div>
</div>
{{/if}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}
