                          {{> components/products/ratings rating=rating}}
                      </span>
                  </p>
              {{/if}}

              <div class="card-text" data-test-info-type="price">
                  <div class="price-visibility{{#or customer (if theme_settings.restrict_to_login '===' true)}} visible{{/or}}">
                    {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                      {{> components/products/price price=price customer=customer}}
                    {{else}}
                  </div>
                  <div class="card-text add-to-cart-button price-visibility{{#or customer (if theme_settings.restrict_to_login '===' true)}} restricted{{/or}}">
                      {{> components/common/login-for-pricing}}
                  {{/or}}
                  </div>
              </div>
              {{> components/products/bulk-discount-rates}}
              {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
              <div class="card-text add-to-cart-button {{#if stock_level '<' 1}}{{#if out_of_stock_message '===' false}} no-button{{/if}}{{/if}}">
                      {{#if show_cart_action}}
                          {{#if has_options}}
                              <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                          {{/if}}
                          {{#if pre_order}}
                              <a href="{{url}}" class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                          {{/if}}
                          {{#if add_to_cart_url }}
                              <a href="{{add_to_cart_url}}" class="button button--small card-figcaption-button">{{lang 'products.add_to_cart'}}</a>
                          {{/if}}
                          {{#if out_of_stock_message }}
                              <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                          {{/if}}
                          {{#if stock_level '<' 1}}
                            {{#if out_of_stock_message '===' false}}
                              <a href="{{url}}" class="button button--small card-figcaption-button false-stock-message" data-product-id="{{id}}">View Details</a>
                            {{/if}}
                          {{/if}}
                      {{/if}}
                      {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                        {{#if price}}
                        {{ else }}
                          <div class="card-text add-to-cart-button call-for-price">
                            <a href="{{url}}" class="button button--small card-figcaption-button call-for-price desktop" data-product-id="{{id}}"><span class="line-one">{{theme_settings.card_call_for_price_text}}</span><span class="line-two">{{theme_settings.card_call_for_price_phone_number}}</span></a>
                            <a href="tel:{{theme_settings.card_call_for_price_phone_number}}" class="button button--small card-figcaption-button call-for-price mobile" data-product-id="{{id}}"><span class="line-one">{{theme_settings.card_call_for_price_text}}</span><span class="line-two">{{theme_settings.card_call_for_price_phone_number}}</span></a>
                          </div>
                          <!-- <div class="current-price"></div> -->
                        {{/if}}
                      {{/or}}
              </div>
              {{/or}}
          </div>
          {{/or}}
          {{/or}}
        {{/if}}
</article>
{{/if}}
