                    {{#if theme_settings.card-brand-name '===' true}}
                     {{#if brand.name}}
                     {{#if theme_settings.card-sku '===' true}}
                     {{#if sku}}
                       <span class="brand-sku-divider">|</span>
                     {{/if}}
                     {{/if}}
                     {{/if}}
                   {{/if}}
                  <h4 class="card-text sku {{#if theme_settings.card-sku-2-lines '===' true}} two-lines {{/if}}">
                    {{#if theme_settings.card-sku '===' true}}
                    {{#if sku}}
                    <a class="sku-link" href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>
                      <span class="sku-label">{{lang 'sku'}}:</span>
                      <span class="sku-value" data-product-sku>{{sku}}</span>
                    </a>
                    {{/if}}
                    {{/if}}
                  </h4>
                </div>
                <h4 class="listItem-title">
                    <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>{{name}}</a>
                </h4>
                {{#if summary}}
                    <p>{{{summary}}}</p>
                {{/if}}
            </div>
            <div class="listItem-actions">
                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                    {{#if price}}
                        <div class="listItem-price">{{> components/products/price price=price}}</div>
                        {{else}}
                          <div class="msrp-sale-regular-price-section"></div>
                          <div class="card-text add-to-cart-button call-for-price current-price">
                            <div class="call-for-price desktop" data-product-id="{{id}}"><span class="line-one">{{theme_settings.card_call_for_price_text}}</span><span class="line-two">{{theme_settings.card_call_for_price_phone_number}}</span></div>
                            <a href="tel:{{theme_settings.card_call_for_price_phone_number}}" class="call-for-price mobile" data-product-id="{{id}}"><span class="line-one">{{theme_settings.card_call_for_price_text}}</span><span class="line-two">{{theme_settings.card_call_for_price_phone_number}}</span></a>
                          </div>
                    {{/if}}
                    {{#if show_cart_action}}
                        {{#if has_options}}
                            <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
